<script>
export default {
  // props:['id'],
  data() {
    return {
      number: 2,
      roomMore: true,
      fac_list: [
        {
          val: "电梯",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "地铁",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "便利店",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "ATM",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "停车场",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "健身房",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "会议室",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "餐厅",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "活动场地",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "安全监控",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "快递代收",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "家居保洁",
          url:
            "url(data:image/png;base64,)",
        },
        {
          val: "公共厨房",
          url:
            "url(data:image/png;base64,)",
        },
      ],
      tab_selected: -1,
      tab_list: ["优惠活动", "基础设施", "户型", "地址及周边配套"],
      active: 0,
      item: {},
      det: [],
      imageSRC: [],
      imageSRC2:
        "http://vky-oss.inboyu.com/upload/39ec0b96-b3fd-5f26-b309-77de6c051230.jpg",
      opacity: 0.5,
      translateX: 0,
      rooms: [],
      scroll:'',
    };
  },
  methods: {
    hide() {
      this.roomMore = true;
      this.number = 2;
    },
    more() {
      this.roomMore = false;
      this.number = this.rooms.length;
    },
    selected(e, i) {
      this.tab_selected = i;
      if(i==0){
        window.scrollTo(0,document.querySelector('.benefit').offsetTop-170)
      }else if(i==1){
        window.scrollTo(0,document.querySelector('.fac').offsetTop-170)
      }else if(i==2){
        window.scrollTo(0,document.querySelector('.roomtype').offsetTop-170)
      }else if(i==3){
        window.scrollTo(0,document.querySelector('#map1').offsetTop-170)
      }
    },
    show(e, i) {
      this.active = i;
      this.imageSRC2 = this.imageSRC[this.active];
    },
    prev() {
      if (this.translateX < 0) {
        this.translateX += 120;
      } else {
        return;
      }
    },
    next() {
      if (this.translateX > -(this.imageSRC.length - 4) * 120) {
        this.translateX -= 120;
      } else {
        return;
      }
    },
  },
  mounted() {
    this.$store.commit("changeheaderactive", 2);

    //获取URL中的动态参数
    let id = this.$route.params.id;
    //将此id发送到web服务器以获取到当前门店的详细信息
    this.axios
      .get("/shopnameINFO", {
        params: {
          id: id,
        },
      })
      .then((result) => {
        this.rooms = result.data.layout;
        this.item = result.data.result[0];
        this.det = this.item.detail.split("，");
        this.imageSRC = this.item.images.split("，");

        // *****百度地图
        //创建Map实例
        var map = new BMap.Map("allmap1");
        //
        var localSearch = new BMap.LocalSearch(map);
        //将拿到的地址获取到 经纬度
        var keyword = result.data.result[0].address;
        localSearch.setSearchCompleteCallback(function (searchResult) {
          var poi = searchResult.getPoi(0);
          // 初始化地图,设置中心点坐标
          map.centerAndZoom(result.data.result[0].address, 17);
          //添加鼠标滚动缩放
          map.enableScrollWheelZoom();
          //添加缩略图控件
          map.addControl(
            new BMap.OverviewMapControl({
              isOpen: false,
              anchor: BMAP_ANCHOR_BOTTOM_RIGHT,
            })
          );
          //添加缩放平移控件
          map.addControl(new BMap.NavigationControl());
          //添加比例尺控件
          map.addControl(new BMap.ScaleControl());
          //添加地图类型控件
          map.addControl(new BMap.MapTypeControl());
          //设置标注的经纬度
          var marker = new BMap.Marker(
            new BMap.Point(poi.point.lng, poi.point.lat)
          );
          //把标注添加到地图上
          map.addOverlay(marker);
        });
        localSearch.search(keyword);
        //
        // *****
      });
  },
};
</script>

